mapper.select:
SELECT TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,(SELECT EMAIL FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS EMAIL ,FN_CELLPHONE((SELECT MOBILE_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ)) AS MOBILE_NO ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,FN_PL_M_Z_ID(TLM.PL_M_Z_ID) AS PL_M_Z_ID ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,FN_CD_DTL('ADD001',TLM.ADDR) AS ADDR_NM ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TO_DATE(TLM.CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(TLM.CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,TO_DATE(TLM.ENTRUST_DATE,'YYYYMMDD') AS ENTRUST_DATE ,TO_DATE(TLM.COM_CONT_DATE,'YYYYMMDD') AS COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,TLM.PL_MERCHANT_NO AS ORIGIN_PL_MERCHANT_NO ,FN_PL_M_Z_ID(TLM.PL_MERCHANT_NO) AS PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TO_DATE(TLM.CORP_FOUND_DATE,'YYYYMMDD') AS CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CHK_YN ,TLM.CRE_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,FN_CD_DTL('DRP001',TLM.PL_HIST_CD) AS PL_HIST_CD_NM ,TLM.PL_HIST_TXT ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.MASTER_SEQ = #{masterSeq}

SELECT EXC_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,POSITION_NM ,EXC_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,PL_EDU_NO ,TO_DATE(CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,FULL_TM_STAT ,FN_CD_DTL('FTM001',FULL_TM_STAT) AS FULL_TM_STAT_NM ,EXPERT_STAT ,FN_CD_DTL('EXP001',EXPERT_STAT) AS EXPERT_STAT_NM ,FILE_SEQ FROM TB_LC_MAS01_IMWON
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXC_SEQ ASC

SELECT EXP_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,EXP_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,PL_EDU_NO ,TO_DATE(CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,FILE_SEQ FROM TB_LC_MAS01_EXPERT
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXP_SEQ ASC

SELECT OPER_SEQ ,MASTER_SEQ ,OPER_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,FILE_SEQ FROM TB_LC_MAS01_IT
AND MASTER_SEQ = #{masterSeq}
ORDER BY OPER_SEQ ASC

mapper.update:
UPDATE tb_lc_mas01
SET
	pl_stat = #{plStat}

		, pl_reg_stat = #{plRegStat}


		, cre_app_date = TO_CHAR(NOW(), 'YYYYMMDD')


		,pl_hist_txt = #{plHistTxt}


		,com_reg_date = TO_CHAR(NOW(), 'YYYYMMDD')

	, upd_seq = #{updSeq}
	, upd_timestamp = NOW()
WHERE
	master_seq = #{masterSeq}

mapper.delete:
DELETE FROM TB_LC_FILE_CHECK
WHERE
	FILE_SEQ = #{fileSeq}
AND CHECK_CD = #{checkCd}

mapper.update:
UPDATE tb_lc_mas01
SET
	chk_yn = #{chkYn}
	, upd_seq = #{updSeq}
	, upd_timestamp = NOW()
WHERE master_seq = #{masterSeq}
