mapper.update:
update tb_lc_schedule
set
	status = #{status}

		, error = #{error}

	, upd_seq = 0
	, upd_timestamp = NOW()
	, end_time = NOW()
where schedule_seq = #{scheduleSeq}

update tb_lc_mas01
set
	pre_lc_num 		= #{preLcNum}
	, pre_reg_yn	= #{preRegYn}
	, upd_timestamp = NOW()
	, upd_seq 		= 0
	, pl_reg_stat	= '1'
	, pl_stat		= '2'
where
	master_seq = #{masterSeq}

mapper.select:
INSERT INTO tb_lc_schedule_hist
(
	schedule_name
	, req_cnt
	, start_time

		, property01


		, property02


		, property03


		, property04


		, property05

	, reg_seq
	, reg_timestamp
)
VALUES(
	#{scheduleName}
	, #{reqCnt}
	, NOW()

		, #{property01}


		, #{property02}


		, #{property03}


		, #{property04}


		, #{property05}

	, 0
	, NOW()
)
RETURNING schedule_hist_seq AS scheduleHistSeq

mapper.update:
UPDATE tb_lc_schedule_hist
SET
	success_cnt			= #{successCnt}
	, end_time			= NOW()

		, property01	= #{property01}


		, property02	= #{property02}


		, property03	= #{property03}


		, property04	= #{property04}


		, property05	= #{property05}

	, upd_seq			= 0
	, upd_timestamp		= NOW()
WHERE
	schedule_name = #{scheduleName}
AND schedule_hist_seq = #{scheduleHistSeq}

