mapper.select:
SELECT FILE_SEQ ,FILE_GRP_SEQ ,FILE_TYPE ,FILE_EXT ,FILE_ORG_NM ,FILE_PATH ,FILE_SAVE_NM ,CONCAT(FILE_ORG_NM,'.',FILE_EXT) AS FILE_FULL_NM ,USE_YN ,TO_CHAR(REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP FROM TB_LC_FILE01
AND FILE_GRP_SEQ = #{fileGrpSeq}
AND USE_YN = 'N'
AND USE_YN = 'Y'
AND FILE_TYPE = #{histType}

SELECT FILE_SEQ ,FILE_GRP_SEQ ,FILE_TYPE ,FILE_EXT ,FILE_ORG_NM ,FILE_PATH ,FILE_SAVE_NM ,CONCAT(FILE_ORG_NM,'.',FILE_EXT) AS FILE_FULL_NM ,USE_YN ,TO_CHAR(REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP FROM TB_LC_FILE01
AND FILE_GRP_SEQ = #{fileGrpSeq}
AND FILE_PATH = #{filePath}

mapper.update:
UPDATE TB_LC_FILE01
SET
	 USE_YN 		= 'N'
	,UPD_SEQ 		= #{updSeq}
	,UPD_TIMESTAMP 	= NOW()

	AND FILE_SEQ 	= #{fileSeq}

mapper.delete:
    	DELETE FROM TB_LC_FILE01

			AND FILE_GRP_SEQ = #{fileGrpSeq}

			AND FILE_PATH = #{filePath}
