mapper.insert:

insert into tb_lc_file01
(
	file_ext, file_org_nm, file_path, file_save_nm, reg_timestamp, upd_timestamp, use_yn

		, file_type			

)
values(
	#{fileExt}, #{fileOrgNm}, #{filePath}, #{fileSaveNm}, now(), now(), 'Y'

		#{fileType}

)

mapper.select:
SELECT
	 CODE_MST_CD
	,CODE_DTL_CD
	,CODE_DTL_NM
	,CODE_DTL_DESC
	,PROPERTY01
	,PROPERTY02
	,PROPERTY03
	,PROPERTY04
	,PROPERTY05
	,REG_DATE
	,REG_NO
FROM tbl_code_dtl
WHERE
	CODE_MST_CD = #{codeMstCd}

	AND PROPERTY01 = #{property01}


	AND PROPERTY02 = #{property02}


	AND PROPERTY03 = #{property03}


	AND PROPERTY04 = #{property04}


	AND PROPERTY05 = #{property05}
