mapper.select:
SELECT
TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.PASSWORD ,TLM.COM_CODE ,fn_com_code(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_NAME ,TLM.EMAIL ,TLM.EXTENSION_NO ,TLM.MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TO_DATE(TLM.JOIN_DT, 'YYYYDDMM') AS joinDt ,TLM.ROLE_NAME ,TLM.TERMS_YN ,TLM.TERMS_DATE ,TLM.APPR_STAT
,TLM.FILE_SEQ ,TLM.FAIL_CNT ,TLM.CRE_YN ,TLM.DROP_YN ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MEMBERS TLM
AND TLM.COM_CODE = #{comCode}
AND TLM.APPR_STAT = #{apprStat}

SELECT TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.PASSWORD ,TLM.COM_CODE ,fn_com_code(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_NAME ,TLM.EMAIL ,TLM.EXTENSION_NO ,TLM.MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TO_DATE(TLM.JOIN_DT, 'YYYYDDMM') AS joinDt ,TLM.ROLE_NAME ,TLM.TERMS_YN ,TLM.TERMS_DATE ,TLM.APPR_STAT
,TLM.FILE_SEQ ,TLM.FAIL_CNT ,TLM.CRE_YN ,TLM.DROP_YN ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MEMBERS TLM
AND MEMBER_SEQ = #{memberSeq}

mapper.update:
UPDATE TB_LC_MEMBERS
SET	
	 APPR_STAT = #{apprStat}
	,UPD_SEQ   = #{updSeq}

	AND MEMBER_SEQ 	= #{memberSeq}
