mapper.select:
SELECT
TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.PASSWORD ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_NAME ,TLM.EMAIL ,FN_CELLPHONE(TLM.EXTENSION_NO) AS EXTENSION_NO ,FN_CELLPHONE(TLM.MOBILE_NO) AS MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TO_DATE(TLM.JOIN_DT, 'YYYYMMDD') AS JOIN_DT ,TLM.ROLE_NAME ,TLM.TERMS_YN ,TLM.TERMS_DATE ,TLM.APPR_STAT ,FN_CD_DTL('MEM001',TLM.APPR_STAT) AS APPR_STAT_NM ,TLM.FILE_SEQ ,TLM.FAIL_CNT ,TLM.CRE_YN ,TLM.DROP_YN ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP ,TLM.MSG FROM TB_LC_MEMBERS TLM WHERE COM_CODE != 3000001
AND TLM.COM_CODE = #{comCode}
AND TLM.APPR_STAT = #{apprStat}

mapper.update:
UPDATE TB_LC_MEMBERS
SET	
	 APPR_STAT 		= #{apprStat}
	,ROLE_NAME 		= #{roleName}
	,MSG	   		= #{msg}
	,UPD_SEQ   		= #{updSeq}
	,UPD_TIMESTAMP	= NOW()

	AND MEMBER_SEQ 	= #{memberSeq}

mapper.select:
SELECT COUNT(*)
FROM TB_LC_COMPANY
WHERE COM_CODE = #{comCode}
