SELECT
TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.PASSWORD ,TLM.COM_CODE ,fn_com_code(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_NAME ,TLM.EMAIL ,TLM.MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TLM.JOIN_DT ,TLM.APPR_YN ,TLM.FILE_SEQ ,TLM.FAIL_CNT ,TLM.CRE_YN ,TLM.DROP_YN ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MEMBERS TLM
AND TLM.MEMBER_SEQ = #{memberSeq}
