mapper.update:
UPDATE TB_LC_MEMBERS SET CRE_GRP = #{creGrp} /*협회그룹*/ , MEMBER_NAME = #{memberName} /*담당자이름*/ , MEMBER_ID = #{memberId} /*담당자ID*/ , UPD_SEQ = #{updSeq} /*수정자 시퀀스*/ , UPD_TIMESTAMP = NOW() /*수정일시*/
, PASSWORD = #{password} /*비밀번호*/
WHERE MEMBER_SEQ = #{memberSeq}

mapper.select:
SELECT
	COUNT(*) 
FROM TB_LC_MEMBERS
	WHERE MEMBER_ID = #{memberId}

		AND MEMBER_SEQ !	= #{memberSeq}

mapper.insert:
INSERT INTO TB_LC_WORK
(
	 MEMBER_SEQ
	, COM_CODE
)
VALUES
(
	 #{memberSeq}
	, #{comCode}
)
