mapper.select:
SELECT
MEM.MEMBER_SEQ , MEM.MEMBER_ID , MEM.PASSWORD , MEM.MEMBER_NAME , MEM.EMAIL , fn_cellphone(MEM.EXTENSION_NO) AS EXTENSION_NO , fn_cellphone(MEM.MOBILE_NO) AS MOBILE_NO , TO_DATE(MEM.JOIN_DT, 'YYYYDDMM') AS JOIN_DT , MEM.CRE_YN , MEM.CRE_GRP FROM TB_LC_MEMBERS MEM WHERE MEM.COM_CODE = 1
AND MEMBER_ID = #{memberId}
AND MEMBER_NAME = #{memberName}
AND CRE_YN = #{creYn}
