mapper.select:
SELECT
MEM.MEMBER_SEQ ,MEM.MEMBER_ID ,MEM.PASSWORD ,MEM.MEMBER_NAME ,MEM.EMAIL ,REGEXP_REPLACE(MEM.EXTENSION_NO, '(02|031|032|033|041|042|043|051|052|053|054|055|061|062|063|064'||'|010|011|016|017|018|019'||'|030|050|060|070|080)'||'(.+)(.{4})','\1-\2-\3') AS EXTENSION_NO ,REGEXP_REPLACE(MEM.MOBILE_NO, '(02|.{3})(.+)(.{4})', '\1-\2-\3') AS MOBILE_NO ,TO_DATE(MEM.JOIN_DT, 'YYYYDDMM') AS JOIN_DT ,MEM.CRE_YN FROM TB_LC_MEMBERS MEM WHERE MEM.COM_CODE = 1
AND MEMBER_ID = #{memberId}
AND MEMBER_NAME = #{memberName}
AND CRE_YN = #{creYn}
