mapper.select:
SELECT 
	COUNT(*)
FROM TB_LC_EDU01
WHERE CAREER_TYP 			= #{careerTyp}
AND USER_NAME 				= #{userName}
AND RIGHT(USER_BIRTH,6) 	= #{userBirth}
AND USER_SEX 				= #{userSex}
AND PROCESS_CD 				= #{processCd}

AND EXAM_CERT_NO			= #{srchInput}


AND DEPLOMA_NO 				= #{srchInput}
