mapper.select:
SELECT
TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.PASSWORD ,TLM.COM_CODE ,TLM.MEMBER_NAME ,TLM.EMAIL ,TLM.MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TLM.JOIN_DT ,TLM.APPR_YN ,TLM.FILE_SEQ ,TLM.FAIL_CNT ,TLM.CRE_YN ,TLM.DROP_YN ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MEMBERS TLM
AND TLM.COM_CODE = #{comCode}
AND TLM.APPR_YN = #{apprYn}

SELECT
TLM.MEMBER_SEQ ,TLM.MEMBER_ID ,TLM.MEMBER_NAME ,TLM.EMAIL ,TLM.MOBILE_NO ,TLM.DEPT_NM ,TLM.POSITION_NM ,TLM.JOIN_DT FROM TB_LC_MEMBERS TLM
AND TLM.MEMBER_SEQ = #{memberSeq}

UPDATE TB_LC_MEMBERS
SET
	
		MEMBER_ID 		= #{memberId}
	
	
		,PASSWORD 		= #{password}
	
	
		,DEPT_NM 		= #{deptNm}
	
	
		,MEMBER_NAME 	= #{memberName}
	
	
		,POSITION_NM 	= #{positionNm}
	
	
		,EMAIL 			= #{email}
	
	
		,MOBILE_NO 		= #{mobileNo}
	

	AND MEMBER_SEQ 		= #{memberSeq}
