mapper.update:
		UPDATE TB_LC_MAS01
		SET
			 USER_SEQ 		= #{userSeq}
			,REG_PATH 		= CASE 
								WHEN PL_REG_STAT != '1' THEN 'F'
								ELSE REG_PATH
			 				  END
			,UPD_SEQ 		= #{userSeq}
   			,UPD_TIMESTAMP 	= NOW()

			AND PL_CLASS 		= #{plClass}
			AND PL_MERCHANT_NO 	= #{plMerchantNo}
			AND USE_YN 			= 'Y'
			AND USER_SEQ IS NULL

mapper.select:
SELECT EXC_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,POSITION_NM ,EXC_NAME ,PL_M_Z_ID ,PL_EDU_NO ,CASE WHEN CAREER_START_DATE IS NULL THEN NULL WHEN CAREER_START_DATE = '' THEN NULL ELSE TO_DATE(CAREER_START_DATE,'YYYYMMDD') END AS CAREER_START_DATE ,CASE WHEN CAREER_END_DATE IS NULL THEN NULL WHEN CAREER_END_DATE = '' THEN NULL ELSE TO_DATE(CAREER_END_DATE,'YYYYMMDD') END AS CAREER_END_DATE ,FULL_TM_STAT ,FN_CD_DTL('FTM001',FULL_TM_STAT) AS FULL_TM_STAT_NM ,EXPERT_STAT ,FN_CD_DTL('EXP001',EXPERT_STAT) AS EXPERT_STAT_NM ,FILE_SEQ FROM TB_LC_MAS01_IMWON
AND USE_YN = 'Y' AND MASTER_SEQ = #{masterSeq}
ORDER BY EXC_SEQ ASC

SELECT EXP_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,EXP_NAME ,PL_M_Z_ID ,PL_EDU_NO ,CASE WHEN CAREER_START_DATE IS NULL THEN NULL WHEN CAREER_START_DATE = '' THEN NULL ELSE TO_DATE(CAREER_START_DATE,'YYYYMMDD') END AS CAREER_START_DATE ,CASE WHEN CAREER_END_DATE IS NULL THEN NULL WHEN CAREER_END_DATE = '' THEN NULL ELSE TO_DATE(CAREER_END_DATE,'YYYYMMDD') END AS CAREER_END_DATE ,FILE_SEQ FROM TB_LC_MAS01_EXPERT
AND USE_YN = 'Y' AND MASTER_SEQ = #{masterSeq}
ORDER BY EXP_SEQ ASC

SELECT OPER_SEQ ,MASTER_SEQ ,OPER_NAME ,PL_M_Z_ID ,FILE_SEQ FROM TB_LC_MAS01_IT
AND USE_YN = 'Y' AND MASTER_SEQ = #{masterSeq}
ORDER BY OPER_SEQ ASC

mapper.update:
   		UPDATE TB_LC_MAS01
   		SET
   			PL_STAT			= #{plStat}
			,UPD_SEQ		= #{regSeq}
			,UPD_TIMESTAMP	= NOW()

				, pl_hist_txt = #{plHistTxt}

		WHERE
			MASTER_SEQ = #{masterSeq}

   		UPDATE TB_LC_MAS01
   		SET
   			PL_STAT			= #{plStat}
			,UPD_SEQ		= #{regSeq}
			,UPD_TIMESTAMP	= NOW()

			,PL_HIST_TXT 	= #{plHistTxt}


			,PL_HIST_CD		= #{plHistCd}
			,COM_HAEJI_DATE	= TO_CHAR(NOW(),'YYYYMMDD')

			,pl_stat_req_seq = #{regSeq}
			,pl_stat_req_path = '2'
		WHERE
			MASTER_SEQ = #{masterSeq}

   		UPDATE TB_LC_MAS01
   		SET
   			PL_STAT			= #{plStat}
			,UPD_SEQ		= #{regSeq}
			,UPD_TIMESTAMP	= NOW()
			,PL_HIST_TXT 	= null
			,PL_HIST_CD		= null
			,COM_HAEJI_DATE	= null
			,pl_stat_req_seq = #{regSeq}
			,pl_stat_req_path = null
		WHERE
			MASTER_SEQ = #{masterSeq}

UPDATE TB_LC_VIOLATION
SET
	APPLY_YN = 'D'
	,UPD_SEQ		= #{updSeq}
	,UPD_TIMESTAMP	= NOW()
WHERE VIOLATION_SEQ = #{violationSeq}
