mapper.select:
SELECT
TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,FN_PL_M_Z_ID(TLM.PL_M_Z_ID) AS PL_M_Z_ID ,TLM.PL_M_Z_ID_EDU ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TLM.CAREER_START_DATE ,TLM.CAREER_END_DATE ,TLM.ENTRUST_DATE ,TLM.COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,FN_PL_M_Z_ID(TLM.PL_MERCHANT_NO) AS PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TLM.CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TO_DATE(TLM.COM_REG_DATE,'YYYYMMDD') AS COM_REG_DATE ,TO_DATE(TLM.CRE_APP_DATE,'YYYYMMDD') AS CRE_APP_DATE ,TO_DATE(TLM.CRE_LICENSE_DATE,'YYYYMMDD') AS CRE_LICENSE_DATE ,TO_DATE(TLM.PAY_DATE,'YYYYMMDD') AS PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.COM_CODE = (SELECT COM_CODE FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = #{regSeq}) -- 로그인한 사용자와 동일한 회원사 코드인 정보만 출력 AND TLM.PL_STAT NOT IN ('1','2','6') -- 처리상태가 미요청, 승인요청, 취소이 아닌 것
AND TLM.PL_CLASS = #{plClass}
AND TLM.PL_PRODUCT = #{plProduct}
AND TLM.PL_STAT = #{plStat}
AND TLM.PL_REG_STAT = #{plRegStat}

mapper.update:
UPDATE TB_LC_MAS01
SET
	 PL_STAT 		= #{plStat}
	,UPD_SEQ 		= #{updSeq}

	,PL_HIST_TXT 	= #{plHistTxt}


	,PL_REG_STAT 	= #{plRegStat}

	,UPD_TIMESTAMP 	= NOW()

	AND MASTER_SEQ 	= #{masterSeq}

mapper.select:
SELECT
TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,FN_PL_M_Z_ID(TLM.PL_M_Z_ID) AS PL_M_Z_ID ,TLM.PL_M_Z_ID_EDU ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TLM.CAREER_START_DATE ,TLM.CAREER_END_DATE ,TLM.ENTRUST_DATE ,TLM.COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,FN_PL_M_Z_ID(TLM.PL_MERCHANT_NO) AS PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TLM.CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,CASE WHEN TLM.PL_CLASS = '1' AND TLM.FILE_SEQ IS NOT NULL AND (SELECT COUNT(1) FROM TB_LC_FILE01 TLF WHERE TLF.FILE_GRP_SEQ = TLM.FILE_SEQ AND TLF.USE_YN = 'Y' AND TLF.FILE_TYPE IN ('1','2','5','6','7')) = 5 THEN 'Y' WHEN TLM.PL_CLASS = '2' AND TLM.FILE_SEQ IS NOT NULL AND (SELECT COUNT(1) FROM TB_LC_FILE01 TLF WHERE TLF.FILE_GRP_SEQ = TLM.FILE_SEQ AND TLF.USE_YN = 'Y' AND TLF.FILE_TYPE IN ('1','2','3','5','6')) = 5 THEN 'Y' ELSE 'N' END AS FILE_COMP_YN ,CASE WHEN TLM.PL_CLASS = '1' AND TLM.FILE_SEQ IS NOT NULL AND (SELECT COUNT(1) FROM TB_LC_FILE01 TLF WHERE TLF.FILE_GRP_SEQ = TLM.FILE_SEQ AND TLF.USE_YN = 'Y' AND TLF.FILE_TYPE IN ('1','2','5','6','7')) = 5 THEN '완료' WHEN TLM.PL_CLASS = '2' AND TLM.FILE_SEQ IS NOT NULL AND (SELECT COUNT(1) FROM TB_LC_FILE01 TLF WHERE TLF.FILE_GRP_SEQ = TLM.FILE_SEQ AND TLF.USE_YN = 'Y' AND TLF.FILE_TYPE IN ('1','2','3','5','6')) = 5 THEN '완료' ELSE '미완료' END AS FILE_COMP_TXT ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.COM_CODE = (SELECT COM_CODE FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = #{regSeq}) -- 로그인한 사용자와 동일한 회원사 코드인 정보만 출력 AND TLM.PL_STAT IN ('1','2') -- 처리상태가 미요청, 승인요청인 것
AND TLM.PL_CLASS = #{plClass}
AND TLM.PL_PRODUCT = #{plProduct}
AND TLM.PL_STAT = #{plStat}

mapper.update:
UPDATE TB_LC_MAS01
SET
	 PL_STAT		= #{plStat}
	,UPD_SEQ 		= #{updSeq}
	,UPD_TIMESTAMP	= NOW()

	AND MASTER_SEQ 	= #{masterSeq}

mapper.select:
SELECT TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,(SELECT EMAIL FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS EMAIL ,FN_CELLPHONE((SELECT MOBILE_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ)) AS MOBILE_NO ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,FN_PL_M_Z_ID(TLM.PL_M_Z_ID) AS PL_M_Z_ID ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,FN_CD_DTL('ADD001',TLM.ADDR) AS ADDR_NM ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TO_DATE(TLM.CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(TLM.CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,TO_DATE(TLM.ENTRUST_DATE,'YYYYMMDD') AS ENTRUST_DATE ,TO_DATE(TLM.COM_CONT_DATE,'YYYYMMDD') AS COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,FN_PL_M_Z_ID(TLM.PL_MERCHANT_NO) AS PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TO_DATE(TLM.CORP_FOUND_DATE,'YYYYMMDD') AS CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,TLM.PL_HIST_TXT ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.MASTER_SEQ = #{masterSeq}

SELECT EXC_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,POSITION_NM ,EXC_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,PL_EDU_NO ,TO_DATE(CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,FULL_TM_STAT ,FN_CD_DTL('FTM001',FULL_TM_STAT) AS FULL_TM_STAT_NM ,EXPERT_STAT ,FN_CD_DTL('EXP001',EXPERT_STAT) AS EXPERT_STAT_NM ,FILE_SEQ FROM TB_LC_MAS01_IMWON
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXC_SEQ ASC

SELECT EXP_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,EXP_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,PL_EDU_NO ,TO_DATE(CAREER_START_DATE,'YYYYMMDD') AS CAREER_START_DATE ,TO_DATE(CAREER_END_DATE,'YYYYMMDD') AS CAREER_END_DATE ,FILE_SEQ FROM TB_LC_MAS01_EXPERT
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXP_SEQ ASC

SELECT OPER_SEQ ,MASTER_SEQ ,OPER_NAME ,FN_PL_M_Z_ID(PL_M_Z_ID) AS PL_M_Z_ID ,FILE_SEQ FROM TB_LC_MAS01_IT
AND MASTER_SEQ = #{masterSeq}
ORDER BY OPER_SEQ ASC

mapper.update:
   		UPDATE TB_LC_MAS01
   		SET
   			  FILE_SEQ 			= #{fileSeq}

   			 ,PL_M_NAME 		= #{plMName}


   			,PL_M_Z_ID 			= REPLACE(#{plMZId}, '-', '')


   			,PL_CELLPHONE 		= REPLACE(#{plCellphone}, '-', '')


   			,ADDR 				= #{addr}


   			,ADDR_DETAIL		= #{addrDetail}


   			,PL_EDU_NO 			= #{plEduNo}


   			,CAREER_START_DATE 	= REPLACE(#{careerStartDate}, '-', '')


   			,CAREER_END_DATE 	= REPLACE(#{careerEndDate}, '-', '')


   			,COM_CONT_DATE 		= REPLACE(#{comContDate}, '-', '')


   			,ENTRUST_DATE 		= REPLACE(#{entrustDate}, '-', '')



   			,PL_MERCHANT_NAME	= #{plMerchantName}


   			,PL_CEO_NAME 		= #{plCeoName}


   			,PL_MERCHANT_NO 	= REPLACE(#{plMerchantNo}, '-', '')


   			,CORP_FOUND_DATE 	= REPLACE(#{corpFoundDate}, '-', '')


   			,CAPITAL 			= #{capital}

   			,UPD_SEQ 			= #{updSeq}
   			,UPD_TIMESTAMP 		= NOW()

	    	AND MASTER_SEQ 		= #{masterSeq}
	    	AND PL_STAT			!= '2'			-- 승인요청상태가 아닐 때만 수정 가능

	UPDATE TB_LC_MAS01_IMWON
	SET
		 CAREER_TYP			= #{careerTyp}
		,POSITION_NM		= #{positionNm}
		,EXC_NAME			= #{excName}
		,PL_M_Z_ID			= REPLACE(#{plMZId}, '-', '')
		,PL_EDU_NO			= #{plEduNo}
		,CAREER_START_DATE	= REPLACE(#{careerStartDate}, '-', '')
		,CAREER_END_DATE	= REPLACE(#{careerEndDate}, '-', '')
		,FULL_TM_STAT		= #{fullTmStat}
		,EXPERT_STAT		= #{expertStat}
		,FILE_SEQ			= #{fileSeq}

    	AND EXC_SEQ = #{excSeq}

   		UPDATE TB_LC_MAS01_EXPERT
		SET
			 CAREER_TYP			= #{careerTyp}
			,EXP_NAME			= #{expName}
			,PL_M_Z_ID			= REPLACE(#{plMZId}, '-', '')
			,PL_EDU_NO			= #{plEduNo}
			,CAREER_START_DATE	= REPLACE(#{careerStartDate}, '-', '')
			,CAREER_END_DATE	= REPLACE(#{careerEndDate}, '-', '')
			,FILE_SEQ			= #{fileSeq}

	    	AND EXP_SEQ = #{expSeq}

   		UPDATE TB_LC_MAS01_IT
   		SET
   			 OPER_NAME		= #{operName}
			,PL_M_Z_ID		= REPLACE(#{plMZId}, '-', '')
			,FILE_SEQ		= #{fileSeq}

	    	AND OPER_SEQ 	= #{operSeq}
