mapper.select:
SELECT
TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,TLM.PL_M_Z_ID ,TLM.PL_M_Z_ID_EDU ,TLM.PL_CELLPHONE ,TLM.ADDR ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TLM.CAREER_START_DATE ,TLM.CAREER_END_DATE ,TLM.ENTRUST_DATE ,TLM.COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,TLM.PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TLM.CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,CASE WHEN TLM.FILE_SEQ IS NOT NULL THEN '완료' ELSE '미완료' END AS FILE_COMP_TXT ,TLM.REG_TIMESTAMP ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.PL_STAT != '6' -- 처리상태가 완료가 아닌 것만 리스트에 보여짐(완료건은 모집인 조회 및 변경 메뉴에서 보여짐)
AND TLM.PL_CLASS = #{plClass}
AND TLM.PL_PRODUCT = #{plProduct}
AND TLM.PL_STAT = #{plStat}

mapper.update:
UPDATE TB_LC_MAS01
SET
	 PL_STAT = #{plStat}
	,UPD_SEQ = #{updSeq}

	AND MASTER_SEQ 	= #{masterSeq}

mapper.select:
SELECT TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,(SELECT EMAIL FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS EMAIL ,(SELECT MOBILE_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS MOBILE_NO ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,TLM.PL_M_Z_ID ,TLM.PL_CELLPHONE ,TLM.ADDR ,FN_CD_DTL('ADD001',TLM.ADDR) AS ADDR_NM ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,TLM.CAREER_START_DATE ,TLM.CAREER_END_DATE ,TLM.ENTRUST_DATE ,TLM.COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,TLM.PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,TLM.CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,TLM.COM_HAEJI_DATE ,TLM.CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TLM.REG_TIMESTAMP ,TLM.UPD_SEQ ,TLM.UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.MASTER_SEQ = #{masterSeq}

mapper.update:
   		UPDATE TB_LC_MAS01
   		SET

   			 PL_M_NAME 			= #{plMName}


   			,PL_M_Z_ID 			= REPLACE(#{plMZId}, '-', '')


   			,PL_CELLPHONE 		= REPLACE(#{plCellphone}, '-', '')


   			,ADDR 				= #{addr}


   			,ADDR_DETAIL		= #{addrDetail}


   			,PL_EDU_NO 			= #{plEduNo}


   			,CAREER_START_DATE 	= REPLACE(#{careerStartDate}, '-', '')


   			,CAREER_END_DATE 	= REPLACE(#{careerEndDate}, '-', '')


   			,COM_CONT_DATE 		= REPLACE(#{comContDate}, '-', '')


   			,ENTRUST_DATE 		= REPLACE(#{entrustDate}, '-', '')

   			,FILE_SEQ 			= #{fileSeq}
   			,UPD_SEQ 			= #{updSeq}
   			,UPD_TIMESTAMP 		= NOW()

	    	AND MASTER_SEQ 		= #{masterSeq}
	    	AND PL_STAT			!= '2'			-- 승인요청상태가 아닐 때만 수정 가능
