mapper.select:
SELECT TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MASTER_TO_ID ,TLM.SUB_YN ,TLM.PRE_REG_YN ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,(SELECT EMAIL FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS EMAIL ,FN_CELLPHONE((SELECT MOBILE_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ)) AS MOBILE_NO ,FN_CELLPHONE((SELECT EXTENSION_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ)) AS EXTENSION_NO ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.PL_WORK ,TLM.CORP_USER_YN ,TLM.CAREER_TYP ,TLM.API_CAREER_YN ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CON_NUM ,TLM.CI ,TLM.PL_M_NAME ,TLM.PL_M_Z_ID ,TLM.PL_M_Z_ID AS PL_M_Z_ID_ENC ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,FN_CD_DTL('ADD001',TLM.ADDR) AS ADDR_NM ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,case when TLM.CAREER_START_DATE = '' then null when TLM.CAREER_START_DATE is null then null else TO_DATE(TLM.CAREER_START_DATE,'YYYYMMDD') end AS CAREER_START_DATE ,case when TLM.CAREER_END_DATE = '' then null when TLM.CAREER_END_DATE is null then null else TO_DATE(TLM.CAREER_END_DATE,'YYYYMMDD') end AS CAREER_END_DATE ,case when TLM.ENTRUST_DATE = '' then null when TLM.ENTRUST_DATE is null then null else TO_DATE(TLM.ENTRUST_DATE,'YYYYMMDD') end AS ENTRUST_DATE ,case when TLM.COM_CONT_DATE = '' then null when TLM.COM_CONT_DATE is null then null else TO_DATE(TLM.COM_CONT_DATE,'YYYYMMDD') end AS COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,TLM.PL_MERCHANT_NO AS ORIGIN_PL_MERCHANT_NO ,TLM.PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,case when TLM.CORP_FOUND_DATE = '' then null when TLM.CORP_FOUND_DATE is null then null else TO_DATE(TLM.CORP_FOUND_DATE,'YYYYMMDD') end AS CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.VOTING_STOCK_CNT ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.FIRST_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,FN_CD_DTL('DRP001',TLM.PL_HIST_CD) AS PL_HIST_CD_NM ,TLM.PL_HIST_TXT ,case when TLM.COM_HAEJI_DATE = '' then null when TLM.COM_HAEJI_DATE is null then null else TO_DATE(TLM.COM_HAEJI_DATE,'YYYYMMDD') end AS COM_HAEJI_DATE ,case when TLM.CRE_HAEJI_DATE = '' then null when TLM.CRE_HAEJI_DATE is null then null else TO_DATE(TLM.CRE_HAEJI_DATE,'YYYYMMDD') end AS CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01 TLM
AND TLM.MASTER_SEQ = #{masterSeq}

SELECT EXC_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,POSITION_NM ,EXC_NAME ,PL_M_Z_ID ,PL_EDU_NO ,case when CAREER_START_DATE = '' then null when CAREER_START_DATE is null then null else TO_DATE(CAREER_START_DATE,'YYYYMMDD') end AS CAREER_START_DATE ,case when CAREER_END_DATE = '' then null when CAREER_END_DATE is null then null else TO_DATE(CAREER_END_DATE,'YYYYMMDD') end AS CAREER_END_DATE ,FULL_TM_STAT ,FN_CD_DTL('FTM001',FULL_TM_STAT) AS FULL_TM_STAT_NM ,EXPERT_STAT ,FN_CD_DTL('EXP001',EXPERT_STAT) AS EXPERT_STAT_NM ,FILE_SEQ FROM TB_LC_MAS01_IMWON
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXC_SEQ ASC

SELECT EXP_SEQ ,MASTER_SEQ ,CAREER_TYP ,FN_CD_DTL('CAR001',CAREER_TYP) AS CAREER_TYP_NM ,EXP_NAME ,PL_M_Z_ID ,PL_EDU_NO ,case when CAREER_START_DATE = '' then null when CAREER_START_DATE is null then null else TO_DATE(CAREER_START_DATE,'YYYYMMDD') end AS CAREER_START_DATE ,case when CAREER_END_DATE = '' then null when CAREER_END_DATE is null then null else TO_DATE(CAREER_END_DATE,'YYYYMMDD') end AS CAREER_END_DATE ,FILE_SEQ FROM TB_LC_MAS01_EXPERT
AND MASTER_SEQ = #{masterSeq}
ORDER BY EXP_SEQ ASC

SELECT OPER_SEQ ,MASTER_SEQ ,OPER_NAME ,PL_M_Z_ID ,FILE_SEQ FROM TB_LC_MAS01_IT
AND MASTER_SEQ = #{masterSeq}
ORDER BY OPER_SEQ ASC

mapper.update:
UPDATE tb_lc_mas01
SET
	pl_stat = #{plStat}

		, pl_reg_stat = #{plRegStat}


		, cre_app_date = TO_CHAR(NOW(), 'YYYYMMDD')


		,pl_hist_txt = #{plHistTxt}


		,com_reg_date = TO_CHAR(NOW(), 'YYYYMMDD')

	, upd_seq = #{updSeq}
	, upd_timestamp = NOW()
WHERE
	master_seq = #{masterSeq}

mapper.select:
SELECT TLM.MASTER_HIST_SEQ ,TLM.MASTER_SEQ ,TLM.COM_CODE ,FN_COM_CODE(TLM.COM_CODE) AS COM_CODE_NM ,TLM.MASTER_TO_ID ,TLM.SUB_YN ,TLM.PRE_REG_YN ,TLM.MEMBER_SEQ ,FN_MEMBER_NAME(TLM.MEMBER_SEQ) AS MEMBER_NM ,(SELECT EMAIL FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ) AS EMAIL ,FN_CELLPHONE((SELECT MOBILE_NO FROM TB_LC_MEMBERS WHERE MEMBER_SEQ = TLM.MEMBER_SEQ)) AS MOBILE_NO ,TLM.PL_CLASS ,FN_CD_DTL('CLS001',TLM.PL_CLASS) AS PL_CLASS_NM ,TLM.CAREER_TYP ,TLM.API_CAREER_YN ,FN_CD_DTL('CAR001',TLM.CAREER_TYP) AS CAREER_TYP_NM ,TLM.PL_PRODUCT ,FN_CD_DTL('PRD001',TLM.PL_PRODUCT) AS PL_PRODUCT_NM ,TLM.PL_REG_STAT ,FN_CD_DTL('REG001',TLM.PL_REG_STAT) AS PL_REG_STAT_NM ,TLM.PL_STAT ,FN_CD_DTL('MAS001',TLM.PL_STAT) AS PL_STAT_NM ,TLM.PL_REGIST_NO ,TLM.CI ,TLM.PL_M_NAME ,TLM.PL_M_Z_ID ,FN_CELLPHONE(TLM.PL_CELLPHONE) AS PL_CELLPHONE ,TLM.ADDR ,FN_CD_DTL('ADD001',TLM.ADDR) AS ADDR_NM ,TLM.ADDR_DETAIL ,TLM.PL_EDU_NO ,case when TLM.CAREER_START_DATE = '' then null when TLM.CAREER_START_DATE is null then null else TO_DATE(TLM.CAREER_START_DATE,'YYYYMMDD') end AS CAREER_START_DATE ,case when TLM.CAREER_END_DATE = '' then null when TLM.CAREER_END_DATE is null then null else TO_DATE(TLM.CAREER_END_DATE,'YYYYMMDD') end AS CAREER_END_DATE ,case when TLM.ENTRUST_DATE = '' then null when TLM.ENTRUST_DATE is null then null else TO_DATE(TLM.ENTRUST_DATE,'YYYYMMDD') end AS ENTRUST_DATE ,case when TLM.COM_CONT_DATE = '' then null when TLM.COM_CONT_DATE is null then null else TO_DATE(TLM.COM_CONT_DATE,'YYYYMMDD') end AS COM_CONT_DATE ,TLM.PL_MERCHANT_NAME ,TLM.PL_CEO_NAME ,TLM.PL_MERCHANT_NO AS ORIGIN_PL_MERCHANT_NO ,TLM.PL_MERCHANT_NO ,TLM.PL_BUSINESS_NO ,TLM.COMP_PHONE_NO ,case when TLM.CORP_FOUND_DATE = '' then null when TLM.CORP_FOUND_DATE is null then null else TO_DATE(TLM.CORP_FOUND_DATE,'YYYYMMDD') end AS CORP_FOUND_DATE ,TLM.CAPITAL ,TLM.VOTING_STOCK_CNT ,TLM.PL_PAY_STAT
,TLM.COM_REG_DATE ,TLM.CRE_APP_DATE ,TLM.FIRST_APP_DATE ,TLM.CRE_LICENSE_DATE ,TLM.PAY_DATE ,TLM.SEND_MSG ,TLM.PL_HIST_CD ,FN_CD_DTL('DRP001',TLM.PL_HIST_CD) AS PL_HIST_CD_NM ,TLM.PL_HIST_TXT ,case when TLM.COM_HAEJI_DATE = '' then null when TLM.COM_HAEJI_DATE is null then null else TO_DATE(TLM.COM_HAEJI_DATE,'YYYYMMDD') end AS COM_HAEJI_DATE ,case when TLM.CRE_HAEJI_DATE = '' then null when TLM.CRE_HAEJI_DATE is null then null else TO_DATE(TLM.CRE_HAEJI_DATE,'YYYYMMDD') end AS CRE_HAEJI_DATE ,TLM.FILE_SEQ ,TO_CHAR(TLM.REG_TIMESTAMP,'YYYY-MM-DD') AS REG_TIMESTAMP ,TLM.UPD_SEQ ,TO_CHAR(TLM.UPD_TIMESTAMP,'YYYY-MM-DD') AS UPD_TIMESTAMP FROM TB_LC_MAS01_HIST TLM
AND TLM.MASTER_HIST_SEQ = #{masterHistSeq}
AND TLM.MASTER_SEQ = #{masterSeq}
AND TLM.PL_M_NAME != #{searchPlMName}
AND TLM.PL_M_Z_ID != #{searchPlMZId}
AND TLM.PL_CELLPHONE != #{searchPlCellphone}
ORDER BY TLM.MASTER_HIST_SEQ DESC LIMIT 1
