mapper.update:
UPDATE tb_lc_users
SET
	corp_appr_yn = 'Y'
	, corp_appr_dt = NOW()
	, upd_seq = #{updSeq}
	, upd_timestamp = NOW()
	, appr_txt = #{apprTxt}
WHERE user_seq = #{userSeq}

mapper.select:
SELECT
Z.* FROM ( SELECT users.user_seq , users.user_id , users.password , users.user_name , users.mobile_no , users.pl_class , FN_CD_DTL('CLS001',users.PL_CLASS) AS PL_CLASS_NM , users.pl_m_z_id , users.email , users.user_ci , users.corp_appr_yn , TO_CHAR(users.join_dt,'YYYY-MM-DD') AS join_dt , users.terms_yn , TO_CHAR(users.terms_dt,'YYYY-MM-DD') AS terms_dt , users.drop_yn , TO_CHAR(users.drop_dt,'YYYY-MM-DD') AS drop_dt , users.inactive_yn , TO_CHAR(users.inactive_dt,'YYYY-MM-DD') AS inactive_dt , TO_CHAR(users.last_login_dt,'YYYY-MM-DD HH:MI:SS') AS last_login_dt , TO_CHAR(users.corp_appr_dt,'YYYY-MM-DD') AS corp_appr_dt , users.fail_cnt , TO_CHAR(users.fail_stop_dt,'YYYY-MM-DD') AS fail_stop_dt , CASE WHEN fail_stop_dt is null THEN 'N' ELSE 'Y' END AS fail_stop_yn ,corp.corp_seq ,corp.pl_merchant_name ,corp.pl_merchant_no ,corp.pl_business_no ,corp.path_typ ,corp.pass_yn , (select b.disqual_val from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '9' limit 1) as dis9 , (select b.disqual_val from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '10' limit 1) as dis10 , (select b.disqual_val from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '11' limit 1) as dis11 , (select b.disqual_val from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '12' limit 1) as dis12 , (select b.disqual_val from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '13' limit 1) as dis13 , (select TO_CHAR(b.upd_timestamp,'YYYY-MM-DD HH24:MI:SS') from tb_lc_users_disqual b where corp.user_seq = b.user_seq and b.disqual_cd = '9' limit 1) as upd_dis1 , case when dis.dis1 > 0 then 'Y' when dis.dis1 is null then '' else 'N' end dis1 , case when dis.dis2 > 0 then 'Y' when dis.dis2 is null then '' else 'N' end dis2 FROM tb_lc_corp corp left join tb_lc_users users on users.user_seq = corp.user_seq left join ( select fi.user_seq , SUM(fi.dis1) as dis1 , SUM(fi.dis2) as dis2 from ( select imwon.user_seq as user_seq ,dis.user_seq as imwon_seq ,case when disqual_cd = '7' and disqual_val = 'N' then 0 when disqual_cd = '7' and disqual_val = 'Y' then 1 end dis1 ,case when disqual_cd = '8' and disqual_val = 'N' then 0 when disqual_cd = '8' and disqual_val = 'Y' then 1 end dis2 from tb_lc_users_disqual dis , ( select im.user_seq as imwon_seq , d.user_seq as user_seq from tb_lc_mas01_imwon im ,( select b.master_seq , a.user_seq from tb_lc_users a inner join tb_lc_mas01 b on a.user_seq = b.user_seq ) d where im.master_seq = d.master_seq group by d.user_seq, im.user_seq ) imwon where dis.user_seq = imwon.imwon_seq and master_seq = 0 ) fi group by fi.user_seq ) dis on dis.user_seq = users.user_seq ORDER BY users.user_seq desc )Z
AND Z.user_id LIKE '%' || #{userId} || '%'
AND Z.user_name LIKE '%' || #{userName} || '%'
AND Z.mobile_no LIKE '%' || #{mobileNo} || '%'
AND Z.pl_merchant_name LIKE '%' || #{plMerchantName} || '%'
AND Z.pl_merchant_no LIKE '%' || #{plMerchantNo} || '%'
AND Z.dis9 = #{dis9}
AND Z.dis10 = #{dis10}
AND Z.dis11 = #{dis11}
AND Z.dis12 = #{dis12}
AND Z.dis13 = #{dis13}
AND Z.dis1 = #{dis1}
AND Z.dis2 = #{dis2}
AND Z.pass_yn = #{passYn}
AND Z.corp_appr_yn = #{corpApprYn}
AND Z.join_dt::TIMESTAMP >= #{srchDate1}::TIMESTAMP
AND Z.join_dt::TIMESTAMP < #{srchDate2}::TIMESTAMP + INTERVAL '1 day'

mapper.update:
UPDATE tb_lc_users_disqual
SET
	disqual_val = #{disVal}
	, upd_seq = #{regSeq}
	, upd_timestamp = NOW()
WHERE
	USER_SEQ = (SELECT user_seq FROM tb_lc_users where pl_m_z_id = #{plMZId} AND user_name = #{userName} LIMIT 1)
AND disqual_cd = #{disCd}

UPDATE tb_lc_users_disqual
SET
	disqual_val = #{disVal}
	, upd_seq = #{regSeq}
	, upd_timestamp = NOW()
WHERE
	USER_SEQ = (SELECT user_seq FROM tb_lc_corp where pl_merchant_no = #{plMerchantNo} AND pl_merchant_name = #{plMerchantName} LIMIT 1)
AND disqual_cd = #{disCd}

UPDATE tb_lc_users_disqual
SET
	disqual_val = #{disVal}
	, upd_seq = #{regSeq}
	, upd_timestamp = NOW()
WHERE
	USER_SEQ = #{userSeq}
AND disqual_cd = #{disCd}

UPDATE tb_lc_users_disqual
SET
	disqual_val = #{disVal}
	, upd_seq = #{regSeq}
	, upd_timestamp = NOW()
WHERE
	USER_SEQ = #{userSeq}
AND disqual_cd = #{disCd}

UPDATE tb_lc_corp
SET
	pass_yn = #{passYn}
	, upd_seq = #{regSeq}
	, upd_timestamp = NOW()
WHERE
	CORP_SEQ = #{corpSeq}

mapper.select:
SELECT user_seq , disqual_cd as dis_cd , disqual_val as dis_val , use_yn , reg_seq , TO_CHAR(reg_timestamp,'YYYY-MM-DD HH24:MI:SS') as reg_timestamp , upd_seq , TO_CHAR(upd_timestamp,'YYYY-MM-DD HH24:MI:SS') as upd_timestamp FROM tb_lc_users_disqual WHERE user_seq = #{userSeq}
AND disqual_cd in ('7', '8')
AND disqual_cd in ('9', '12', '13')

