mapper.select:
  		SELECT
   			 TLM.MASTER_SEQ
   		FROM TB_LC_MAS01 TLM
		WHERE TLM.PL_CLASS		= '1' 				-- 개인

		AND TLM.MASTER_TO_ID	= #{masterToId}


		AND TLM.CON_NUM			= #{conNum}

		AND TLM.PL_CELLPHONE 	= #{plCellphone}
		AND TLM.PL_REG_STAT 	= '3' 				-- 모집인상태 : 자격취득
		AND TLM.PL_STAT 		= '9' 				-- 처리상태 : 완료
		AND TLM.USE_YN			= 'Y'

   		SELECT
   			TLM.MASTER_SEQ
   		FROM TB_LC_MAS01 TLM
		WHERE TLM.PL_CLASS			= '2' 				-- 법인 

		AND TLM.MASTER_TO_ID		= #{masterToId}


		AND TLM.CON_NUM				= #{conNum}

		AND TLM.PL_MERCHANT_NAME 	= #{plMerchantName}
		AND TLM.PL_REG_STAT 		= '3' 				-- 모집인상태 : 자격취득
		AND TLM.PL_STAT 			= '9' 				-- 처리상태 : 완료
		AND TLM.USE_YN			= 'Y'

mapper.update:
UPDATE TB_LC_MAS01
SET
	 PL_REG_STAT 		= #{plRegStat}

	,PL_REGIST_NO		= #{plRegistNo}		-- 은행연합회 등록번호


	,CON_NUM 			= #{conNum}			-- 은행연합회 계약번호


	,CRE_LICENSE_DATE 	= TO_CHAR(NOW(),'YYYYMMDD')

	,UPD_SEQ 			= #{updSeq}
	,UPD_TIMESTAMP 		= NOW()

	AND MASTER_SEQ 		= #{masterSeq}

mapper.insert:
INSERT INTO TB_LC_MAS01_STEP ( MASTER_SEQ ,PL_REG_STAT ,PL_STAT ,PL_HIST_TXT ,REG_TIMESTAMP ,REG_SEQ ) SELECT MASTER_SEQ ,PL_REG_STAT ,PL_STAT
,CASE WHEN PL_STAT = '4' THEN FN_CD_DTL('DRP001',PL_HIST_CD) WHEN PL_STAT IN ('3','5','6','7','8','10','11','12') THEN PL_HIST_TXT ELSE '' END AS PL_HIST_TXT
,'수수료기납부여부에 따른 자격취득으로 변경' AS PL_HIST_TXT
,NOW() ,#{regSeq} FROM TB_LC_MAS01
AND MASTER_SEQ = #{masterSeq}
AND PL_CLASS = '1' AND PL_M_NAME = #{plMName} AND PL_CELLPHONE = #{plCellphone}
AND PL_CLASS = '2' AND PL_MERCHANT_NO = #{plMerchantNo} AND PL_CEO_NAME = #{plCeoName}
AND PL_REG_STAT = '2' -- 모집인상태 : 승인완료 AND PL_STAT = '9' -- 처리상태 : 완료 AND USE_YN = 'Y'

INSERT INTO TB_LC_MAS01_HIST ( MASTER_SEQ ,MASTER_TO_ID ,COM_CODE ,MEMBER_SEQ ,SUB_YN ,PRE_REG_YN ,PRE_LC_NUM ,PL_REGIST_NO ,CON_NUM ,PL_CLASS ,PL_WORK ,CORP_USER_YN ,CAREER_TYP ,API_CAREER_YN ,PL_PRODUCT ,PL_REG_STAT ,PL_STAT ,CI ,PL_M_NAME ,PL_M_Z_ID ,PL_M_Z_ID_EDU ,PL_CELLPHONE ,ADDR ,ADDR_DETAIL ,PL_EDU_NO ,CAREER_START_DATE ,CAREER_END_DATE ,ENTRUST_DATE ,COM_CONT_DATE ,PL_MERCHANT_NAME ,PL_CEO_NAME ,PL_MERCHANT_NO ,PL_BUSINESS_NO ,COMP_PHONE_NO ,CORP_FOUND_DATE ,CAPITAL ,VOTING_STOCK_CNT ,PL_PAY_STAT ,COM_REG_DATE ,FIRST_APP_DATE ,APP_DATE_HOLD ,CHK_YN ,ADMIN_CHK_YN ,ADMIN_CHK_SEQ ,COM_UPD_DATE ,CRE_APP_FI_DATE ,CRE_APP_DATE ,CRE_LICENSE_DATE ,PAY_DATE ,SEND_MSG ,PL_HIST_CD ,PL_HIST_TXT ,COM_HAEJI_DATE ,CRE_HAEJI_DATE ,FILE_SEQ ,USE_YN ,REG_TIMESTAMP ,UPD_SEQ ,UPD_TIMESTAMP ) SELECT MASTER_SEQ ,MASTER_TO_ID ,COM_CODE ,MEMBER_SEQ ,SUB_YN ,PRE_REG_YN ,PRE_LC_NUM ,PL_REGIST_NO ,CON_NUM ,PL_CLASS ,PL_WORK ,CORP_USER_YN ,CAREER_TYP ,API_CAREER_YN ,PL_PRODUCT ,PL_REG_STAT ,PL_STAT ,CI ,PL_M_NAME ,PL_M_Z_ID ,PL_M_Z_ID_EDU ,PL_CELLPHONE ,ADDR ,ADDR_DETAIL ,PL_EDU_NO ,CAREER_START_DATE ,CAREER_END_DATE ,ENTRUST_DATE ,COM_CONT_DATE ,PL_MERCHANT_NAME ,PL_CEO_NAME ,PL_MERCHANT_NO ,PL_BUSINESS_NO ,COMP_PHONE_NO ,CORP_FOUND_DATE ,CAPITAL ,VOTING_STOCK_CNT ,PL_PAY_STAT ,COM_REG_DATE ,FIRST_APP_DATE ,APP_DATE_HOLD ,CHK_YN ,ADMIN_CHK_YN ,ADMIN_CHK_SEQ ,COM_UPD_DATE ,CRE_APP_FI_DATE ,CRE_APP_DATE ,CRE_LICENSE_DATE ,PAY_DATE ,SEND_MSG ,PL_HIST_CD ,PL_HIST_TXT ,COM_HAEJI_DATE ,CRE_HAEJI_DATE ,FILE_SEQ ,USE_YN ,REG_TIMESTAMP ,UPD_SEQ ,UPD_TIMESTAMP FROM TB_LC_MAS01
AND MASTER_SEQ = #{masterSeq}
AND PL_CLASS = '1' AND PL_M_NAME = #{plMName} AND PL_CELLPHONE = #{plCellphone}
AND PL_CLASS = '2' AND PL_MERCHANT_NO = #{plMerchantNo} AND PL_CEO_NAME = #{plCeoName}
AND PL_REG_STAT = '2' -- 모집인상태 : 승인완료 AND PL_STAT = '9' -- 처리상태 : 완료 AND USE_YN = 'Y'

mapper.update:
UPDATE TB_LC_MAS01
SET
	 PL_REG_STAT 		= '3'
	,PL_STAT			= '9'
	,PRE_REG_YN			= 'Y'
	,UPD_SEQ 			= #{updSeq}
	,UPD_TIMESTAMP 		= NOW()
	,CRE_LICENSE_DATE 	= TO_CHAR(NOW(),'YYYYMMDD')

	,PL_REGIST_NO		= #{plRegistNo}		



	AND PL_CLASS		= '1'
	AND PL_M_NAME 		= #{plMName}
	AND PL_CELLPHONE 	= #{plCellphone}


	AND PL_CLASS		= '2' 
	AND PL_MERCHANT_NO 	= #{plMerchantNo}
	AND PL_CEO_NAME 	= #{plCeoName}

	AND PL_REG_STAT 	= '2' 				-- 모집인상태 : 승인완료
	AND PL_STAT 		= '9' 				-- 처리상태 : 완료
	AND USE_YN			= 'Y'

UPDATE TB_LC_MAS01
SET
	 PRE_REG_YN 	= 'Y'
	,UPD_SEQ 		= #{updSeq}
	,UPD_TIMESTAMP 	= NOW()

	AND MASTER_SEQ 	= #{masterSeq}
